package schemas

import (
	"reflect"

	"github.com/hashicorp/terraform-plugin-sdk/v2/helper/schema"
	"k8s.io/kops/pkg/apis/kops"
)

func ResourceKopsAssets() *schema.Resource {
	return &schema.Resource{
		Schema: map[string]*schema.Schema{
			"container_registry": OptionalString(),
			"file_repository":    OptionalString(),
			"container_proxy":    OptionalString(),
		},
	}
}

func ExpandResourceKopsAssets(in map[string]interface{}) kops.Assets {
	if in == nil {
		panic("expand Assets failure, in is nil")
	}
	return kops.Assets{
		ContainerRegistry: func(in interface{}) *string {
			if reflect.DeepEqual(in, reflect.Zero(reflect.TypeOf(in)).Interface()) {
				return nil
			}
			return func(in interface{}) *string {
				if in == nil {
					return nil
				}
				if _, ok := in.([]interface{}); ok && len(in.([]interface{})) == 0 {
					return nil
				}
				return func(in string) *string {
					return &in
				}(string(ExpandString(in)))
			}(in)
		}(in["container_registry"]),
		FileRepository: func(in interface{}) *string {
			if reflect.DeepEqual(in, reflect.Zero(reflect.TypeOf(in)).Interface()) {
				return nil
			}
			return func(in interface{}) *string {
				if in == nil {
					return nil
				}
				if _, ok := in.([]interface{}); ok && len(in.([]interface{})) == 0 {
					return nil
				}
				return func(in string) *string {
					return &in
				}(string(ExpandString(in)))
			}(in)
		}(in["file_repository"]),
		ContainerProxy: func(in interface{}) *string {
			if reflect.DeepEqual(in, reflect.Zero(reflect.TypeOf(in)).Interface()) {
				return nil
			}
			return func(in interface{}) *string {
				if in == nil {
					return nil
				}
				if _, ok := in.([]interface{}); ok && len(in.([]interface{})) == 0 {
					return nil
				}
				return func(in string) *string {
					return &in
				}(string(ExpandString(in)))
			}(in)
		}(in["container_proxy"]),
	}
}

func FlattenResourceKopsAssetsInto(in kops.Assets, out map[string]interface{}) {
	out["container_registry"] = func(in *string) interface{} {
		return func(in *string) interface{} {
			if in == nil {
				return nil
			}
			return func(in string) interface{} {
				return FlattenString(string(in))
			}(*in)
		}(in)
	}(in.ContainerRegistry)
	out["file_repository"] = func(in *string) interface{} {
		return func(in *string) interface{} {
			if in == nil {
				return nil
			}
			return func(in string) interface{} {
				return FlattenString(string(in))
			}(*in)
		}(in)
	}(in.FileRepository)
	out["container_proxy"] = func(in *string) interface{} {
		return func(in *string) interface{} {
			if in == nil {
				return nil
			}
			return func(in string) interface{} {
				return FlattenString(string(in))
			}(*in)
		}(in)
	}(in.ContainerProxy)
}

func FlattenResourceKopsAssets(in kops.Assets) map[string]interface{} {
	out := map[string]interface{}{}
	FlattenResourceKopsAssetsInto(in, out)
	return out
}
